mapper.select:
SELECT f.UUID AS filmId, f.film_name AS filmName, fi.film_length AS filmLength, f.img_address AS imgAddress, fi.film_imgs AS subAddress,
(SELECT GROUP_CONCAT(fa.role_name SEPARATOR ',') FROM mooc_film_actor_t fa WHERE fa.film_id = f.UUID) AS actors,
( SELECT GROUP_CONCAT(show_name SEPARATOR ',') FROM mooc_cat_dict_t cd WHERE FIND_IN_SET( cd.UUID, ( SELECT REPLACE (TRIM(BOTH '#' FROM fm.film_cats), "#", ",") FROM mooc_film_t fm WHERE fm.UUID = f.UUID ))) AS filmCats FROM mooc_film_t f, mooc_film_info_t fi WHERE f.UUID = fi.film_id AND f.UUID = #{filmId}
